\begin{center}
\begin{tabular}{|c|c|}
\hline
\textbf{Time, $t$ (s)} & \textbf{Mass of crystals, $m_c$ (g)} \\
\hline
0 & 0.0 \\
10 & 5.1 \\
20 & 9.9 \\
30 & 14.4 \\
40 & 18.6 \\
50 & 22.5 \\
\hline
\end{tabular}
\end{center}

